// Get the new ticket data from Extract New Ticket Data node
const newTicket = $('Extract New Ticket Data').item.json;
const newTicketKey = newTicket.newTicketKey;

// Debug: Log the extracted data to help troubleshoot
// console.log('Extracted ticket data:', JSON.stringify(newTicket, null, 2));

// Get all existing tickets from Get Recent Bug Tickets node, EXCLUDING the new ticket
const existingTickets = $input.all()
  .filter(item => item.json.key !== newTicketKey) // Filter out the new ticket
  .map(item => ({
    key: item.json.key,
    summary: item.json.fields.summary,
    description: (item.json.fields.description || '').substring(0, 300),
    status: item.json.fields.status.name,
    components: item.json.fields.components?.map(c => c.name).join(', ') || 'None',
    labels: item.json.fields.labels?.join(', ') || 'None',
    created: item.json.fields.created
  }));

// Parse attachments and identify log files
const attachments = newTicket.newTicketAttachments ? (typeof newTicket.newTicketAttachments === 'string' ? JSON.parse(newTicket.newTicketAttachments) : newTicket.newTicketAttachments) : [];
const logFileExtensions = ['.log', '.txt', '.out', '.err', '.debug', '.trace'];
const logFiles = attachments.filter(att => {
  const filename = (att.filename || att.name || '').toLowerCase();
  return logFileExtensions.some(ext => filename.endsWith(ext)) || 
         filename.includes('log') || 
         filename.includes('error') || 
         filename.includes('debug');
}).slice(0, 3); // Limit to first 3 log files

return {
  json: {
    newTicketKey: newTicket.newTicketKey,
    newTicketSummary: newTicket.newTicketSummary,
    newTicketDescription: newTicket.newTicketDescription,
    newTicketReporter: newTicket.newTicketReporter,
    newTicketPriority: newTicket.newTicketPriority,
    newTicketComponents: newTicket.newTicketComponents,
    newTicketLabels: newTicket.newTicketLabels,
    newTicketAttachments: attachments,
    logFiles: logFiles,
    hasLogFiles: logFiles.length > 0,
    jiraBaseUrl: newTicket.jiraBaseUrl,
    existingTickets: existingTickets,
    existingTicketsCount: existingTickets.length
  }
};

// Combine log file content with ticket data
// Get ticket data from Prepare Data for Analysis (before log file download)
const ticketData = $('Prepare Data for Analysis').item.json;
const logContent = $input.first().json;

// Extract log content (limit to 30000 chars to avoid token limits)
let logFileContent = '';
if (logContent && typeof logContent === 'string') {
  logFileContent = logContent.substring(0, 30000);
} else if (logContent && logContent.body) {
  logFileContent = String(logContent.body).substring(0, 30000);
} else if (logContent && typeof logContent === 'object') {
  logFileContent = JSON.stringify(logContent).substring(0, 30000);
}

// Prepare log file info
const logFiles = ticketData.logFiles || [];
let logFileInfo = '';
if (logFiles.length > 0) {
  logFileInfo = logFiles.map((file, idx) => {
    return `${idx + 1}. ${file.filename || 'unknown'} (${file.size || 0} bytes)`;
  }).join('\n');
}

return {
  json: {
    ...ticketData,
    logFileContent: logFileContent,
    logFileInfo: logFileInfo,
    hasLogContent: logFileContent.length > 0,
    logFileCount: logFiles.length
  }
};

