// Map LLM priority values to Jira priority names
// Jira instance uses: Lowest, Low, Medium, High, Urgent
const data = $input.first().json;
const llmPriority = (data.recommendedPriority || 'Medium').trim();

// Map LLM priorities to your Jira instance's priority names
const priorityMap = {
  'Critical': 'Urgent',
  'Highest': 'Urgent',
  'Urgent': 'Urgent',
  'High': 'High',
  'Medium': 'Medium',
  'Normal': 'Medium',
  'Low': 'Low',
  'Lowest': 'Lowest'
};

// Try to map, fallback to original if not found
let jiraPriority = priorityMap[llmPriority] || llmPriority;

// If still not a standard name, try common alternatives based on keywords
if (!['Urgent', 'High', 'Medium', 'Low', 'Lowest'].includes(jiraPriority)) {
  const lowerPriority = llmPriority.toLowerCase();
  // Map to closest match based on your Jira priorities
  if (lowerPriority.includes('critical') || lowerPriority.includes('highest') || lowerPriority.includes('urgent') || lowerPriority.includes('blocker')) {
    jiraPriority = 'Urgent';
  } else if (lowerPriority.includes('high') || lowerPriority.includes('major')) {
    jiraPriority = 'High';
  } else if (lowerPriority.includes('medium') || lowerPriority.includes('normal')) {
    jiraPriority = 'Medium';
  } else if (lowerPriority.includes('low')) {
    // Distinguish between Low and Lowest
    if (lowerPriority.includes('lowest') || lowerPriority.includes('trivial')) {
      jiraPriority = 'Lowest';
    } else {
      jiraPriority = 'Low';
    }
  } else {
    jiraPriority = 'Medium'; // Default fallback
  }
}

return {
  json: {
    ...data,
    jiraPriority: jiraPriority,
    originalPriority: llmPriority
  }
};
