// Helper function to extract JSON from text that might have extra content
function extractJSON(text) {
  if (typeof text !== 'string') return text;

  // Remove markdown code blocks
  text = text.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();

  // Try to find JSON object in the text
  const jsonMatch = text.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    try {
      return JSON.parse(jsonMatch[0]);
    } catch (e) {
      // If that fails, try the whole text
    }
  }

  return JSON.parse(text);
}

// Get the duplicate check result
const duplicateCheckItem = $('Check for Duplicates1').item.json;
let duplicateCheck;

try {
  const content = duplicateCheckItem.message?.content || duplicateCheckItem.text || duplicateCheckItem.content || JSON.stringify(duplicateCheckItem);
  duplicateCheck = extractJSON(content);
} catch (e) {
  // Default if parsing fails
  duplicateCheck = {
    isDuplicate: false,
    confidence: 'low',
    potentialDuplicates: [],
    reasoning: 'Could not parse duplicate check response',
    similarityScore: 0
  };
}

// Get the analysis result
const analysisItem = $input.first().json;
let analysis;

try {
  const content = analysisItem.message?.content || analysisItem.text || analysisItem.content || JSON.stringify(analysisItem);
  analysis = extractJSON(content);
} catch (e) {
  // Default if parsing fails
  analysis = {
    summary: 'Could not parse analysis response',
    rootCause: { category: 'Unknown', hypothesis: 'Unknown', confidence: 'low', technicalArea: 'Unknown', reasoning: 'Parse error' },
    priority: { recommended: 'Medium', reasoning: 'Default', factors: ['unknown'], userImpact: 'Unknown', urgency: 'Normal' },
    recommendedActions: ['Review ticket manually'],
    estimatedComplexity: 'Unknown'
  };
}

// Get ticket data
const ticketData = $('Prepare Data for Analysis1').item.json;

return {
  json: {
    ticketKey: ticketData.newTicketKey,
    ticketSummary: ticketData.newTicketSummary,
    ticketDescription: ticketData.newTicketDescription,
    ticketReporter: ticketData.newTicketReporter,
    ticketPriority: ticketData.newTicketPriority,
    ticketComponents: ticketData.newTicketComponents,
    ticketLabels: ticketData.newTicketLabels,

    isDuplicate: duplicateCheck.isDuplicate || false,
    duplicateConfidence: duplicateCheck.confidence || 'low',
    potentialDuplicates: duplicateCheck.potentialDuplicates || [],
    duplicateReasoning: duplicateCheck.reasoning || 'No reasoning provided',
    similarityScore: duplicateCheck.similarityScore || 0,

    summary: analysis.summary || 'No summary',
    rootCauseCategory: analysis.rootCause?.category || 'Unknown',
    rootCauseHypothesis: analysis.rootCause?.hypothesis || 'Unknown',
    rootCauseConfidence: analysis.rootCause?.confidence || 'low',
    rootCauseTechnicalArea: analysis.rootCause?.technicalArea || 'Unknown',
    rootCauseReasoning: analysis.rootCause?.reasoning || 'No reasoning',

    recommendedPriority: analysis.priority?.recommended || 'Medium',
    priorityReasoning: analysis.priority?.reasoning || 'No reasoning',
    priorityFactors: analysis.priority?.factors || ['unknown'],
    userImpact: analysis.priority?.userImpact || 'Unknown',
    urgency: analysis.priority?.urgency || 'Normal',

    recommendedActions: analysis.recommendedActions || ['Review manually'],
    estimatedComplexity: analysis.estimatedComplexity || 'Unknown'
  }
};

